/** @format */

import React, { Component } from "react";
import getMovies from "./movie_list";
import 'font-awesome/css/font-awesome.css';

export class Movies extends Component {
  constructor() {
    super();
    this.state = {
      movie: getMovies()
    };
  }

  deleteHandler(key) {
    const movie = this.state.movie.filter(movieKey => movieKey.key !== key)
    console.log(movie);
  }

  render() {
    console.log(this.state.movie);
    return (
      <div>
        <p>Showing {this.state.movie.length} movies in the database</p>
        <table>
          <thead>
            <tr>
              <th>Title</th>
              <th>Genre</th>
              <th>Stock</th>
              <th>Rate</th>
            </tr>
          </thead>
          <tbody>
            {this.state.movie.map(movie => {
              return (
                <tr key={movie.key}>
                  <td>{movie.name}</td>
                  <td>{movie.genre}</td>
                  <td>{movie.stock}</td>
                  <td>{movie.rate}</td>
                  <td><i className="fas fa-heart"></i></td>
                  <td><button onClick={() => this.state.d}>Delete</button></td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    );
  }
}

export default Movies;
