  }

  render() {
    return (
      <div className="container">
        <div>
          <img src={logo} alt="Logo" />
          <h1>Todo List</h1>
          <p>Add a Note to List</p>
          <div className="todo">
            <input
              type="text"
              placeholder="Enter the Notes toDo"
              value={this.state.newItem}
              required
              onChange={(e) => this.updateInput(e.target.value)}
            />
            <button
            onClick={() => this.addItem(this.state.newItem)}
            >AddNote</button>
            <div className="notes">
              <input type="checkbox" checked="" />
              <p>First Note</p>
              <button>Delete</button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
