/** @format */

import React, { Component } from "react";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      newItem: "",
      list: []
    };
  }


  updateItem(updatedValue) {
    this.setState({newItem: updatedValue});
    console.log(updatedValue);
  }
  render() {
    return (
      <div className="container">
        <h1>What do i need to do today ?</h1>
        <p>Items i need to Add</p>
        <p>Check off items once you have done...!</p>
        <hr />
        <div className="notes">
          <div className="input">
            <input type="checkbox" className="checkbox" />
            <input type="text" required value="Enter the toDo" />
          </div>
          <div className="buttons">
            <button className="del">Delete</button>
          </div>
        </div>
        <hr />
        <div className="addItem">
          <input type="text" required placeholder="Enter the toDo item" onChange={e => this.updateItem(e.target.value)}/>
          <button className="add-note" >+ Add</button>
        </div>
      </div>
    );
  }
}

export default App;
