/** @format */

import React, { Component } from "react";
import "./App.css";
import logo from "./favicon.ico";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      newItem: "",
      list: []
    };
  }

  addItem(newValue) {
    if (newValue !== "") {
      console.log("Ajay");
      const newItem = {
        id: Date.now(),
        value: newValue,
        isDone: false
      };
      const list = [...this.state.list];
      list.push(newItem);

      this.setState({ list, newItem: "" });
    }
  }

  updateItem(input) {
    this.setState({ newItem: input });
  }

  render() {
    return (
      <div className="container">
        <div>
          <img src={logo} alt="Logo" />
          <h1>Todo List</h1>
          <p>Add a Note to List</p>
          <div className="todo">
            <input
              type="text"
              placeholder="Enter the Notes toDo"
              value={this.state.newItem}
              required
              onChange={e => this.updateItem(e.target.value)}
            />
            <button
              onClick={() => this.addItem(this.state.newItem)}
              disabled={!this.state.newItem.length}>
              AddNote
            </button>
            {this.state.list.map(value => {
              return (
                <div className="notes">
                  <input type="checkbox" checked="" />
                  <p>{value}</p>
                  <button>Delete</button>
                </div>
              );
            })}
            <div className="notes">
              <input type="checkbox" checked="" />
              <p>First Note</p>
              <button>Delete</button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
