import React, { Component } from "react";
import "./App.css";
import logo from "./favicon.ico";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      newItem: "",
      list: []
    };
  }

  addItem(newItem) {}

  updateItem(input) {
    this.setState({newItem: input});
  }

  render() {
    return (
      <div className="container">
        <div>
          <img src={logo} alt="Logo" />
          <h1>Todo List</h1>
          <p>Add a Note to List</p>
          <div className="todo">
            <input
              type="text"
              placeholder="Enter the Notes toDo"
              value={this.state.newItem}
